var app = angular.module('app', [
  'ngRoute',
  'appControllers'
]);

app.config(['$routeProvider',
  function($routeProvider) {
    $routeProvider.
      when('/', {
        templateUrl: '/client/partials/home.html',
        controller: 'homeCtrl'
      }).
      when('/company', {
        templateUrl: '/client/partials/company.html',
        controller: 'companyCtrl'
      }).
      when('/technologyServices', {
        templateUrl: '/client/partials/technologyServices.html',
        controller: 'servicesCtrl'
      }).
      when('/engineeringServices', {
        templateUrl: '/client/partials/engineeringServices.html',
        controller: 'trainingCtrl'
      }).
      when('/consultingServices', {
        templateUrl: '/client/partials/consultingServices.html',
        controller: 'trainingCtrl'
      }).
      when('/partners', {
        templateUrl: '/client/partials/partners.html',
        controller: 'partnersCtrl'
      }).
      when('/research+development', {
        templateUrl: '/client/partials/research.html',
        controller: 'researchCtrl'
      }).
      otherwise({
        redirectTo: '/'
      });
  }]);