var appControllers = angular.module('appControllers', []);

appControllers.controller('navCtrl', [ '$scope', '$http',
  function($scope, $http) {

    $scope.topNav = [
            ["company", "#/company"],
            ["services", "#/services"],
            ["training", "#/training"],
            ["partners", "#/partners"],
            ["research + development", "#/researchAndDevelopment"],
            ];

}]);

appControllers.controller('homeCtrl', [ '$scope', '$http',
  function($scope, $http) {

  $scope.language = "English";

  $scope.changeLanguage = function() {
    console.log("changing language");
    if ($scope.language == "English"){
      $scope.language = "中文";
    } else {
      $scope.language = "English";
    }
  }
}]);

appControllers.controller('companyCtrl', [ '$scope', '$http',
  function($scope, $http) {

}]);

appControllers.controller('servicesCtrl', [ '$scope', '$location', '$anchorScroll',
  function($scope, $location, $anchorScroll) {
    $scope.goto = function (id) {
      console.log(id);
      $location.hash(id);
      $anchorScroll();
    }
}]);