var appControllers = angular.module('appControllers', []);

appControllers.controller('navCtrl', [ '$scope', '$http',
  function($scope, $http) {

    $scope.topNav = [
            ["company", "#/company"],
            ["technology services", "#/technologyServices"],
            ["engineering services", "#/engineeringServices"],
            ["consulting services", "#/consultingServices"],
            ["partners", "#/partners"],
            ["research + development", "#/research+development"],
            ];
    $scope.getLocation = function() {
      return window.location.href.toString().split(window.location.host)[1];
    }
}]);

appControllers.controller('homeCtrl', [ '$scope', '$http',
  function($scope, $http) {

  $scope.language = "English";

  $scope.changeLanguage = function() {
    console.log("changing language");
    if ($scope.language == "English"){
      $scope.language = "中文";
    } else {
      $scope.language = "English";
    }
  }
}]);

