$(document).ready(function(){
    /** 
     * This part does the "fixed navigation after scroll" functionality
     * We use the jQuery function scroll() to recalculate our variables as the 
     * page is scrolled/
     */
    $(window).scroll(function(){
        var window_top = $(window).scrollTop() + 140; // the "80" should equal the margin-top value for nav.stick
        var div_top = $('#sideNav').offset().top;
        //console.log("window: " + window_top + " : " + "div: " + div_top);
        var windowHeight = $(window).height(); // get the height of the window
        if (window_top > div_top) {
            $('#sideNav').addClass('stick');
        } else if (window_top != div_top || div_top < windowHeight) {
            $('#sideNav').removeClass('stick');
        }
    });

    /**
     * This part causes smooth scrolling using scrollto.js
     * We target all a tags inside the nav, and apply the scrollto.js to it.
     */
    $("nav a").click(function(evn){
        evn.preventDefault();
        var target = $(this).attr("href")
        //console.log(target);
        $('html, body').animate({
          scrollTop: $(target).offset().top - 145
        }, 200);
    });

    /**
     * This part handles the highlighting functionality.
     * We use the scroll functionality again, some array creation and 
     * manipulation, class adding and class removing, and conditional testing
     */
    var aChildren = $("nav li").children(); // find the a children of the list items
    var aArray = []; // create the empty aArray
    for (var i=0; i < aChildren.length; i++) { 
        var aChild = aChildren[i];
        var ahref = $(aChild).attr('href');
        if (ahref != undefined) {
          aArray.push(ahref);
          //console.log(ahref);
        }
    } // this for loop fills the aArray with attribute href values

    $(window).scroll(function(){
        var windowPos = $(window).scrollTop() + 150; // get the offset of the window from the top of page
        var windowHeight = $(window).height(); // get the height of the window
        var docHeight = $(document).height();

        for (var i=0; i < aArray.length; i++) {
            var theID = aArray[i];
            var divPos = $(theID).offset().top; // get the offset of the div from the top of page
            var divHeight = $(theID).height(); // get the height of the div in question
            //console.log(divPos + ":" + divHeight);
            if (windowPos >= divPos && windowPos < (divPos + divHeight)) {
                $("a[href='" + theID + "']").addClass("sideNav-active");
            } else {
                $("a[href='" + theID + "']").removeClass("sideNav-active");
            }
        }

        if(windowPos + windowHeight == docHeight) {
            if (!$("nav li:last-child a").hasClass("nav-active")) {
                var navActiveCurrent = $(".nav-active").attr("href");
                $("a[href='" + navActiveCurrent + "']").removeClass("nav-active");
                $("nav li:last-child a").addClass("nav-active");
            }
        }
    });
});